import React from 'react';
import CustomerSearchForm from "./CustomerSearchForm";


export default function CustomerInfo() {

    const [query, setQuery] = useState("");
    const [search, setSearch] = useState(false);


    if (sessionStorage.getItem("user_role") == 1)
    {
        return (
            <div>
                <h3>Search customers</h3>
                <CustomerSearchForm setQuery={setQuery} setSearch={setSearch}/>
                <CustomerTable query={query} search={search} setSearch={setSearch} setQuery={setQuery}/>
            </div>
        )
    }

    else
    {
        return (
            <div>
                <h1 style={{color: "red", fontWeight: 900, fontSize: 28, marginLeft: 160}}>Unauthorized</h1>
            </div>
        )
    }
    
}
