import 'date-fns';
import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import Link from '@material-ui/core/Link';
import Button from '@material-ui/core/Button';
import {MuiPickersUtilsProvider,KeyboardDatePicker} from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
import { Typography } from '@material-ui/core';


const useStyles = makeStyles(theme => ({
  searchRoot:{
    padding: '8px',
    width: 600,
    backgroundColor: 'navy',
    color: 'white'
  } , 
  searchField: {
      margin: '4px',
      display: 'flex',
      alignItems: 'center',
      width: 500,
      height:50,
    },
    input: {
      marginLeft: theme.spacing(1),
      flex: 1,
    },
    datePicker: {
      margin: '4px',
      width: 500
    },
    divider: {
      height: 40,
      margin: 4,
    },
    searchButton: {
      margin: 4,
      backgroundColor: 'white',
      color: 'navy',
    },
    advancedSearchLink: {
      marginLeft: 10,
      maxHeight: 30,
      color: 'white',
    }
  }));
export default function CabinSearchForm () {
    const classes = useStyles();

    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [destination, setDestination] = useState("");

   
    return(
        <div>
          <Paper className = {classes.searchRoot}>
            <h3>Book your dream holiday today</h3>
            <Paper component = "form" className = {classes.searchField}>
              <InputBase value = {destination}
              className={classes.input}
              label = "destination"
              placeholder="Destination"
              onChange = {(e) => setDestination(e.target.value)}
              />
            </Paper>
            
            <Paper className = {classes.datePicker}>
            <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDatePicker
          disableToolbar
          variant="inline"
          format="MM/dd/yyyy"
          margin="normal"
          id="start_date"
          label="Check in"
          value={startDate}
          onChange={(date) => setStartDate(date)}
          KeyboardButtonProps={{
            'aria-label': 'change date',
          }}
        />
        
         <KeyboardDatePicker
          disableToolbar
          variant="inline"
          format="MM/dd/yyyy"
          margin="normal"
          id="end_date"
          label="Check out"
          value={endDate}
          onChange={(date) => setEndDate(date)}
          KeyboardButtonProps={{
            'aria-label': 'change date',
          }}
        />
         </MuiPickersUtilsProvider><br/>
         </Paper>
         <Button  variant="contained" className = {classes.searchButton}>Search</Button>
    
         <Link href ="/search" className = {classes.advancedSearchLink}>Advanced search</Link>
       
         </Paper>
        </div>
    )
}
