import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Link from '@material-ui/core/Link';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { useHistory } from 'react-router-dom';
import {getToken, removeUserSession} from '../../Utils/Common';
import logo2 from '../../public/photos/logo2.png';

const useStyles = makeStyles(theme => ({


    linksBar: {
        flexGrow: 1,
    },
    link: {
        marginLeft: theme.spacing(2),
        color: "white"
    },
    appBar: {
        background: "navy",
        color: "White"
    }
}));

export default function Navbar(props) {

    const classes = useStyles();
    const token = getToken();
    const username = sessionStorage.getItem('user_username')
    let history = useHistory();

    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    if (!token) {
        return (
            <AppBar className={classes.appBar} position="static">
                <Toolbar>
                    <Typography variant="h6" className={classes.linksBar}>
                        <img src = {logo2} height="42" width="42"/>
                        <Link className={classes.link} href="/">Home</Link>
                        <Link className={classes.link} href="/">News</Link>
                        <Link className={classes.link} href="/">Resorts</Link>
                        <Link className={classes.link} href="/">About us</Link>
                    </Typography>
                    <Button href="/login" color="inherit">Login</Button>
                    <Button href="/register" color="inherit">Register</Button>
                </Toolbar>
            </AppBar>
        )
    }
    else if (token) {
        return (
            <AppBar className={classes.appBar} position="static">
                <Toolbar>
                    <Typography variant="h6" className={classes.linksBar}>
                        <img src = {logo2} height="42" width="42"/>
                        <Link className={classes.link} href="/">Home</Link>
                        <Link className={classes.link} href="/">News</Link>
                        <Link className={classes.link} href="/">Resorts</Link>
                        <Link className={classes.link} href="/">About us</Link>
                    </Typography>
                    <Button color="inherit" aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                        {username}</Button>
                    <Menu
                        id="simple-menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => {handleClose(); history.push('/account_info')}}>My account</MenuItem>
                        <MenuItem onClick={() => { removeUserSession(); window.location.reload() }} >Logout</MenuItem>
                    </Menu>
                </Toolbar>
            </AppBar>
        )
    }
}

