import React, { useState, useEffect } from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import NativeSelect from '@material-ui/core/NativeSelect';
import {getToken, getUserId} from '../../Utils/Common';
import axios from 'axios';
import {MuiPickersUtilsProvider,KeyboardDatePicker} from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';


const useStyles = makeStyles(theme => ({
    formRoot: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        }
    },
    Infotxt: {
        color: 'yellow',
    }
}));

export default function EditReservationDialog(props) {

    const token = getToken();
    
    const classes = useStyles();
    const [dialogOpen, setDialogOpen] = useState(true);

    const [arrivaldate, setArrivaldate] = useState(props.arrivaldate);
    const [leavedate, setLeavedate] = useState(props.leavedate);
    const [pets, setPets] = useState(props.pets);

    const [serverErrorMsg, setServerErrorMsg] = useState("");

    const axiosPutReservation = () => {
        axios.put('http://localhost:3001/reservation/'+ props.id, {
            arrivaldate: arrivaldate,
            leavedate: leavedate,
            pets: pets,
        },
            {
                headers: {
                    token: token
                }
            })
            .then(function (response) {
                console.log(response);
                setDialogOpen(false);
                props.edit(false);
                window.location.reload();
                
                
            })
            .catch(function (error) {
                console.log(error.response);
                setServerErrorMsg(error.response.data.msg);
            });

    }

    const handleClose = () => {
        setDialogOpen(false);
        props.edit(false);
    };

return (
    <div>
        <Dialog open={dialogOpen} onClose={handleClose} aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">Edit cabin info</DialogTitle>
            <DialogContent>
            <form className={classes.formRoot} noValidate autoComplete="off">
            <div style={{display: "flex"}}> 
            <div style={{marginTop: 20, marginLeft: 10}}>
            <p>Pets</p>
            <NativeSelect
                 required id="pets" label="Pets" defaultValue={props.pets}
                  onChange={(e) => {
                      setPets(parseInt(e.target.value, 10));
                  }}>
                 <option value="1">Yes</option>
                 <option value="0">No</option>
                </NativeSelect>
                </div>

                <div style={{marginTop: 30, marginLeft: 10}}>
            <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDatePicker
          disableToolbar
          variant="inline"
          format="dd/MM/yyyy"
          margin="normal"
          id="start_date"
          label="Check in"
          disablePast = "true"
          value={arrivaldate}
          onChange={(date) => setArrivaldate(date)}
          KeyboardButtonProps={{
            'aria-label': 'change date',
          }}
        />
        
         <KeyboardDatePicker
          disableToolbar
          variant="inline"
          format="dd/MM/yyyy"
          margin="normal"
          id="end_date"
          label="Check out"
          disablePast = "true"
          value={leavedate}
          onChange={(date) => setLeavedate(date)}
          KeyboardButtonProps={{
            'aria-label': 'change date',
          }}
        />
         </MuiPickersUtilsProvider>
            </div>


            </div>
            </form>
            </DialogContent>

            <div>
                <p style={{ color: "red", fontWeight: 900, fontSize: 14, marginLeft: 155 }}>{serverErrorMsg}</p>

                <DialogActions>
                    <Button variant="contained" onClick={() => axiosPutReservation()}>Edit</Button>
                    <Button variant="contained" onClick={() => handleClose()}>Cancel</Button>
                </DialogActions>
            </div>
        </Dialog>
    </div>
    )
}
