import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import {getAdmin} from "../../../Utils/Common"


const useStyles = makeStyles({
    cabinDiv: {
        display: 'grid',
        background: 'navy',
        padding: 8,
        maxWidth: 600,
        maxHeight: 400
    },
    tableHeader: {
        backgroundColor: 'navy',
        
    },
    tableHeaderCell: {
        color: 'white'
    },
    agencyInfo: {
        margin: 4,
        height: 250
       
    },
    editButton: {
        margin: 4,
        backgroundColor: 'navy',
        color: 'white',
        minWidth: 50,
    },

});

export default function GetMyCabins(props) {

    const classes = useStyles();

    const [reservations, setReservations] = useState([]);

    const token = sessionStorage.getItem('token');
    const id = props.id;
    
    useEffect(() => {
        const getReservations = () => {
            axios.get("http://localhost:3001/reservation?person_id=" + props.id, {
                headers: {
                    token: token
                }
            })
                .then(function (response) {
                    console.log(response);
                    setReservations(response.data.Reservations)
                })
                .catch(function (error) {
                    console.log(error);
                })
        }
        getReservations();
    }, []);


    if(reservations.length > 0){
    return (
        <div>
           <TableContainer component={Paper}>
                            <Table className={classes.table} aria-label="customized table">
                                <TableHead className={classes.tableHeader}>
                                    <TableRow>
                                        <TableCell className={classes.tableHeaderCell} align="center">Firstname</TableCell>
                                        <TableCell className={classes.tableHeaderCell} align="center">Check in</TableCell>
                                        <TableCell className={classes.tableHeaderCell} align="center">Check out</TableCell>
                                        <TableCell className={classes.tableHeaderCell} align="center">Pets</TableCell>
                                        <TableCell className={classes.tableHeaderCell} align="center">Edit</TableCell>
                                        <TableCell className={classes.tableHeaderCell} align="center">Remove Reservation</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {reservations.map((reservation, i) =>
                                        <TableRow key={i}>
                                        <TableCell align="right">{reservation.firstname}</TableCell>
                                        <TableCell align="right">{reservation.arrivaldate}</TableCell>
                                        <TableCell align="right">{reservation.leavedate}</TableCell>
                                        <TableCell align="right">{reservation.pets === 1 ? "Yes" : "No"}</TableCell>
                                        <TableCell><Button className={classes.editButton}>Edit</Button></TableCell>
                                        <TableCell><Button className={classes.editButton}>Remove</Button></TableCell>
                                    </TableRow>)}
                                </TableBody>
                            </Table>
                        </TableContainer>
        </div>
    )
    }
    else{
        return(
            <div>
                You dont have any reservations
            </div>
        )
    }
}
