import React, {useState} from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import NativeSelect from '@material-ui/core/NativeSelect';
import {getToken, getUserId} from '../../Utils/Common';
import axios from 'axios';


const useStyles = makeStyles(theme => ({
    formRoot: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        }
    },
    Infotxt: {
        color: 'yellow',
    }
}));

export default function BookCabinDialog(props) {

    const token = getToken();
    const user = getUserId();

    const classes = useStyles();

    const [pets, setPets] = useState(2);
    const [petsErrorMsg, setPetsErrorMsg] = useState(2);

    const [serverErrorMsg, setServerErrorMsg] = useState("");

    const [dialogOpen, setDialogOpen] = useState(true);

    const axiosPostReservation = () => {
        axios.post('http://localhost:3001/reservation/', {
            arrivaldate: props.startDate,
            leavedate: props.endDate,
            pets: pets,
            person_id: user,
            cabin_id: props.cabin_id
        },
            {
                headers: {
                    token: token
                }
            })
            .then(function (response) {
                console.log(response);
                setDialogOpen(false);
                props.book(false);   
            })
            .catch(function (error) {
                console.log(error.response);
                setServerErrorMsg(error.response.data.msg);
            });
    }

    const handleClose = () => {
        setDialogOpen(false);
        props.book(false);
    };

    const handleBookClicked = () => {
        let petsValidationError = false;

        if (pets === 2) {
            setPetsErrorMsg("Please select");
            petsValidationError = true;
        }
        else {
            petsValidationError = false;
            setPetsErrorMsg("");
        }

        if (petsValidationError === false) {
            axiosPostReservation();
        }
    }

return (
    <div>
        <Dialog open={dialogOpen} onClose={handleClose} aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">Book cabin</DialogTitle>
            <DialogContent>
                <form className={classes.formRoot} noValidate autoComplete="off">
                <NativeSelect
                 required id="pets" label="Pets"
                  onChange={(e) => {
                      setPets(e.target.value)
                  }}>
                 <option selected disabled>Please select</option>
                 <option value="1">Yes</option>
                 <option value="0">No</option>
                </NativeSelect>
                <br />
                <TextField id="startDate" label="Check in" ReadOnly value={props.startDate} />
                <TextField id="endDate" label="Check Out" ReadOnly value={props.startDate} />

            </form>
            
            </DialogContent>

            <div>
                <p style={{ color: "red", fontWeight: 900, fontSize: 14, marginLeft: 155 }}>{serverErrorMsg}</p>

                <DialogActions>
                    <Button variant="contained" onClick={() => handleBookClicked()}>Book</Button>
                    <Button variant="contained" onClick={() => handleClose()}>Cancel</Button>
                </DialogActions>
            </div>
        </Dialog>
    </div>
    )
}
