import React, {useState} from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import NativeSelect from '@material-ui/core/NativeSelect';
import {getToken, getUserId} from '../../Utils/Common';
import axios from 'axios';
import dateFormat from 'dateformat';
import Box from '@material-ui/core/Box';


const useStyles = makeStyles(theme => ({
    formRoot: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        }
    },
    Infotxt: {
        color: 'yellow',
    }
}));

export default function BookCabinDialog(props) {

    const defaultProps = {
        bgcolor: 'background.paper',
        m: 1,
        border: 2,
        padding: 2,
        borderRadius: 16
    };

    const token = getToken();
    const user = getUserId();

    const classes = useStyles();

    const startDate = dateFormat(props.startDate, "yyyy-mm-dd");

    const endDate = dateFormat(props.endDate, "yyyy-mm-dd");

    const [pets, setPets] = useState(2);
    const [petsErrorMsg, setPetsErrorMsg] = useState("");

    const [serverErrorMsg, setServerErrorMsg] = useState("");

    const [dialogOpen, setDialogOpen] = useState(true);

    const [reserveMsg, setReserveMsg] = useState("");

    const numberOfDays = dateFormat(props.endDate, "dd/mm/yyyy") - dateFormat(props.startDate, "dd/mm/yyyy");
    const totalPrice = numberOfDays * props.price;

    const axiosPostReservation = () => {
        axios.post('http://localhost:3001/reservation/', {
            arrivaldate: startDate,
            leavedate: endDate,
            pets: pets,
            person_id: user,
            cabin_id: props.cabinId
        },
            {
                headers: {
                    token: token
                }
            })
            .then(function (response) {
                console.log(response);
                setReserveMsg("Cabin succesfully reserved");
                setTimeout(() => {
                setDialogOpen(false);
                props.book(false);  
                }, 4500); 
            })
            .catch(function (error) {
                console.log(error.response);
                setServerErrorMsg(error.response.data.msg);
            });
    }

    const handleClose = () => {
        setReserveMsg("");
        setDialogOpen(false);
        props.book(false);
    };

    const handleBookClicked = () => {
        let petsValidationError = false;

        if (pets == 2) {
            setPetsErrorMsg("Please select");
            petsValidationError = true;
        }
        else {
            petsValidationError = false;
            setPetsErrorMsg("");
        }

        if (petsValidationError === false) {
            axiosPostReservation();
        }
    }

    console.log(props.cabinId);

return (
    <div>
        <Dialog open={dialogOpen} onClose={handleClose} aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">Book cabin</DialogTitle>
            <DialogContent>
                <form className={classes.formRoot} noValidate autoComplete="off">
                <NativeSelect
                 required id="pets" label="Pets" error={petsErrorMsg != ""}
                  onChange={(e) => {
                      setPets(parseInt(e.target.value, 10));
                      (e.target.value != 2) ? setPetsErrorMsg("Please select") : setPetsErrorMsg("")
                  }}>
                 <option selected disabled value="2">Pets?</option>
                 <option value="1">Yes</option>
                 <option value="0">No</option>
                </NativeSelect>
                <br />
                <TextField id="startDate" label="Check in" readOnly value={dateFormat(props.startDate, "dd/mm/yyyy")} />
                <TextField id="endDate" label="Check Out" readOnly value={dateFormat(props.endDate, "dd/mm/yyyy")} />

            </form>

            <Box borderColor="darkgreen" {...defaultProps}> 
            
                <p style={{ color: "navy", fontWeight: 900, fontSize: 16, marginLeft: 80 }}>{totalPrice}€</p>

            </Box>
            </DialogContent>

            <div>
                <p style={{ color: "red", fontWeight: 900, fontSize: 14, marginLeft: 80 }}>{serverErrorMsg}</p>
                <p style={{ color: "darkgreen", fontWeight: 900, fontSize: 16, marginLeft: 80 }}>Total Price:{reserveMsg}</p>

                <DialogActions>
                    <Button variant="contained" onClick={() => handleBookClicked()}>Book</Button>
                    <Button variant="contained" onClick={() => handleClose()}>Cancel</Button>
                </DialogActions>
            </div>
        </Dialog>
    </div>
    )
}
